      @orcamento_iten = OrcamentoIten.find params[:id]
    end
  
    def new
      @casein_page_title = 'Add a new orcamento iten'
    	@orcamento_iten = OrcamentoIten.new
    end

    def create
      @orcamento_iten = OrcamentoIten.new orcamento_iten_params
    
      if @orcamento_iten.save
        flash[:notice] = 'Orcamento iten created'
