      else
        flash.now[:warning] = 'There were problems when trying to create a new orcamento iten'
        render :action => :new
      end
    end
  
    def update
      @casein_page_title = 'Update orcamento iten'
      
      @orcamento_iten = OrcamentoIten.find params[:id]
    
      if @orcamento_iten.update_attributes orcamento_iten_params
        flash[:notice] = 'Orcamento iten has been updated'
        redirect_to casein_orcamento_itens_path
      else
