    def cadastro_ajax
      @orcamento = Orcamento.find(params[:orcamento_id])
      @total = (params[:quantidade].to_f * params[:preco_unitario].gsub(",", ".").to_f)
      @orcamento_iten = OrcamentoIten.create(orcamento_id: params[:orcamento_id], quantidade: params[:quantidade], descricao: params[:descricao], preco_unitario: params[:preco_unitario], unidade: params[:unidade], preco_total: @total)
    end

